class ApiCallerPrototype
  
  def initialize(args)
    @sam_api_key      = ENV.fetch('SAM_GOV_API_KEY')
    @conn             = Faraday.new(headers: {'Content-Type' => 'application/json'})
    @current_through  = SamDotGov.maximum(:posted_date)
    @from             = @current_through
    @to               = Time.now
    @hash = {}
  end

  def call_sam
    get_first_chunk
    get_remaining_chunks
    insert_sam_data
  end

  private

    def get_first_chunk
      query = "?api_key=#{@sam_api_key}&limit=500&offset=0&postedFrom=#{@from}&postedTo=#{@to}"

      first_chunk_size = 500
      total_records = @hash[:opportunitiesData]
      remaining = total_records - first_chunk_size
    end
  
    # https://api.sam.gov/prod/opportunities/v2/search?api_key=l6LjJjJDsPMKXyYnne7WUiZVBX8ximfHQzjdwhS0&limit=1000&offset=0&postedFrom=06/09/2023&postedTo=06/09/2023
    def get_data
      query = "?api_key=#{@sam_api_key}&limit=100&offset=0&postedFrom=06/09/2023&postedTo=06/09/2023"
      url = 'https://api.sam.gov/prod/opportunities/v2/search'
      response = @conn.get(url+query)
      @body = response.body
      @hash = HashWithIndifferentAccess.new(JSON.parse(@body))
      @http_status = response.http_status
    end

